function buildCarousel(age, imagesNumber, autoInit) {
    return {
        block: 'carousel',
        js: {
            live: !autoInit,
            opts: {
                mode: 'vertical',
                minSlides: 3,
                maxSlides: 3,
                auto: true,
                moveSlides: 1,
                pager: false,
                speed: 500,
                responsive: false,
                touchEnabled: false
            }
        },
        content: Array.apply(null, Array(imagesNumber)).map(function(img, idx) {
            return {
                elem: 'item',
                content: {
                    block: 'link',
                    js: { url: 'i/' + age + '/orig/' + (idx + 1) + '.jpg' },
                    mods: { pseudo: true },
                    content: {
                        block: 'image',
                        url: 'i/' + age + '/preview/' + (idx + 1) + '.jpg'
                    }
                }
            };
        })
    };
}
